import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './section3.css'

function Section3() {
  const [data, setData] = useState([]);
  const [isUpdating, setIsUpdating] = useState(false);

  const fetchData = async () => {
    try {
      setIsUpdating(true);

      const response = await axios.get('http://127.0.0.1:8000/votes');

      if (Array.isArray(response.data) && response.data.length > 0) {
        setData(response.data);
      } else {
        // Handle the case when no valid data is received
        console.error('No valid data received:', response.data);
        setData([]);
      }

      setIsUpdating(false);
    } catch (error) {
      console.error('Error fetching data:', error);
      setIsUpdating(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const formatBirthday = (dateString) => {
    if (!dateString) {
      return 'N/A';
    }

    const date = new Date(dateString);
    const options = { year: 'numeric', month: 'long', day: 'numeric', hour: '2-digit', minute: '2-digit', second: '2-digit' };
    return date.toLocaleDateString(undefined, options);
  };

  const handleUpdateData = () => {
    fetchData();
  };

  const renderTable = () => {
    if (data.length === 0) {
      return null;
    }

    const headers = Object.keys(data[0]);

    // Filter out the 'id' key
    const filteredHeaders = headers.filter(header => header !== 'id');

    return (
      <div>
        <button onClick={handleUpdateData} disabled={isUpdating}>
          {isUpdating ? 'Updating...' : 'Mettre à jour'}
        </button>
        <table>
          <thead>
            <tr>
              {filteredHeaders.map((header, index) => (
                <th key={index}>{header}</th>
              ))}
            </tr>
          </thead>
          <tbody>
            {data.map((item, rowIndex) => (
              <tr key={rowIndex}>
                {filteredHeaders.map((header, headerIndex) => (
                  <td key={headerIndex}>
                    {header === 'birthday' ? formatBirthday(item[header]) : item[header] || 'N/A'}
                  </td>
                ))}
              </tr>
            ))}
          </tbody>
        </table>
      </div>
    );
  };

  return (
    <div className="section">
      <h2>Section 3</h2>
      {data.length > 0 ? (
        renderTable()
      ) : (
        <p>No valid data available.</p>
      )}
    </div>
  );
}

export default Section3;
