import React, { useState, useEffect } from 'react';
import axios from 'axios';
import './section3.css';

function Section3() {
  const [data, setData] = useState([]);
  const [isUpdating, setIsUpdating] = useState(false);

  const [editVote, setEditVote] = useState(null);
  const [editedFields, setEditedFields] = useState({
    first_name: '',
    last_name: '',
    gender: '',
    size: '',
    weight: '',
    birthday: '',
  });

  const fetchData = async () => {
    try {
      setIsUpdating(true);

      const response = await axios.get('http://127.0.0.1:8000/votes');

      if (Array.isArray(response.data) && response.data.length > 0) {
        setData(response.data);
      } else {
        console.error('No valid data received:', response.data);
        setData([]);
      }

      setIsUpdating(false);
    } catch (error) {
      console.error('Error fetching data:', error);
      setIsUpdating(false);
    }
  };

  useEffect(() => {
    fetchData();
  }, []);

  const formatBirthday = (dateString) => {
    if (!dateString) {
      return 'N/A';
    }

    const date = new Date(dateString);
    const options = {
      year: 'numeric',
      month: 'long',
      day: 'numeric',
      hour: '2-digit',
      minute: '2-digit',
      second: '2-digit',
    };
    return date.toLocaleDateString(undefined, options);
  };

  const columnMappings = {
    'Prénom': 'first_name',
    'Nom de famille': 'last_name',
    'Sexe': 'gender',
    'Taille(cm)': 'size',
    'Poids (kg)': 'weight',
    'Birthday': 'birthday',
  };

  const columns = Object.keys(columnMappings);

  const handleEditClick = (vote) => {
    setEditVote(vote);
    const birthday = new Date(vote.birthday);
    const formattedDate = birthday.toISOString().split('T')[0]; // Get 'YYYY-MM-DD' date format
    const formattedTime = birthday.toTimeString().split(' ')[0]; 
    setEditedFields({
      first_name: vote.first_name,
      last_name: vote.last_name,
      gender: vote.gender,
      size: vote.size,
      weight: vote.weight,
      birthday: formattedDate,
      birthtime: formattedTime,
    });
  };

  const handleFieldChange = (fieldName, value) => {
    setEditedFields({ ...editedFields, [fieldName]: value });
  };

  const handleUpdateClick = async () => {
    try {
      const response = await axios.put(`http://127.0.0.1:8000/update_vote/${editVote.id}`, editedFields);
      console.log('Update response:', response);

      const updatedData = data.map((item) => (item.id === editVote.id ? { ...item, ...editedFields } : item));
      setData(updatedData);
      setEditVote(null);
    } catch (error) {
      console.error('Error updating vote:', error);
    }
  };

  const handleUpdateData = () => {
    fetchData();
  };

  const renderTable = () => {
    if (data.length === 0) {
      return null;
    }

    return (
      <div>
        <button onClick={handleUpdateData} disabled={isUpdating}>
          {isUpdating ? 'Updating...' : 'Mettre à jour'}
        </button>
        <table>
          <thead>
            <tr>
              {columns.map((column, index) => (
                <th key={index}>{column}</th>
              ))}
              <th>Action</th>
            </tr>
          </thead>
          <tbody>
            {data.map((item, rowIndex) => (
              <tr key={rowIndex}>
                {columns.map((column, columnIndex) => (
                  <td key={columnIndex}>
                    {columnMappings[column] === 'birthday'
                      ? formatBirthday(item[columnMappings[column]])
                      : item[columnMappings[column]] || 'N/A'}
                  </td>
                ))}
                <td>
                  <button onClick={() => handleEditClick(item)}>Edit</button>
                </td>
              </tr>
            ))}
          </tbody>
        </table>
        {editVote && (
  <div className="edit-modal">
    <div className="modal-content">
      <h3>Edit Vote</h3>
      <div className="form-fields">
        {columns.map((column, index) => (
          <div key={index}>
            <label>{column}:</label>
            <span>{editVote[columnMappings[column]] || 'N/A'}</span>
          </div>
        ))}
      </div>
      <div className="input-fields">
        <label>First Name:</label>
        <input
          type="text"
          value={editedFields.first_name}
          onChange={(e) => handleFieldChange('first_name', e.target.value)}
        />
        <label>Last Name:</label>
        <input
          type="text"
          value={editedFields.last_name}
          onChange={(e) => handleFieldChange('last_name', e.target.value)}
        />
        <label>Gender:</label>
        <input
          type="text"
          value={editedFields.gender}
          onChange={(e) => handleFieldChange('gender', e.target.value)}
        />
        <label>Size:</label>
        <input
          type="number"
          value={editedFields.size}
          onChange={(e) => handleFieldChange('size', e.target.value)}
        />
        <label>Weight:</label>
        <input
          type="number"
          value={editedFields.weight}
          onChange={(e) => handleFieldChange('weight', e.target.value)}
        />
        <label>Birthday:</label>
        <input
          type="datetime-local"
          value={`${editedFields.birthday}T${editedFields.birthtime}`}
          onChange={(e) => handleFieldChange('birthday', e.target.value)}
        />
      </div>
      <div className="button-container">
        <button onClick={handleUpdateClick}>Update</button>
        <button onClick={() => setEditVote(null)}>Cancel</button>
      </div>
    </div>
  </div>
)}


      </div>
    );
  };

  return (
    <div className="section3">
      <h2>Section 3</h2>
      {data.length > 0 ? (
        <div>
          {renderTable()}
        </div>
      ) : (
        <p>No valid data available.</p>
      )}
    </div>
  );
}

export default Section3;
