import React, { useState, useEffect } from 'react';
import axios from 'axios';

function Section3() {
  const [data, setData] = useState([]);

  useEffect(() => {
    // Make a GET request to retrieve data from the server
    axios.get('http://127.0.0.1:8000/votes')
      .then((response) => {
        if (Array.isArray(response.data) && response.data.length > 0) {
          setData(response.data);
        } else {
          // Handle the case when no valid data is received
          console.error('No valid data received:', response.data);
          setData([]);
        }
      })
      .catch((error) => {
        console.error('Error fetching data:', error);
        setData([]);
      });
  }, []);

  const formatBirthday = (dateString) => {
    if (!dateString) {
      return 'N/A';
    }

    const date = new Date(dateString);
    const options = { year: 'numeric', month: 'long', day: 'numeric' };
    return date.toLocaleDateString(undefined, options);
  };

  const renderTable = () => {
    if (data.length === 0) {
      return null;
    }

    const headers = Object.keys(data[0]);

    // Filter out the 'id' key
    const filteredHeaders = headers.filter(header => header !== 'id');

    return (
      <table>
        <thead>
          <tr>
            {filteredHeaders.map((header, index) => (
              <th key={index}>{header}</th>
            ))}
          </tr>
        </thead>
        <tbody>
          {data.map((item, rowIndex) => (
            <tr key={rowIndex}>
              {filteredHeaders.map((header, headerIndex) => (
                <td key={headerIndex}>
                  {header === 'birthday' ? formatBirthday(item[header]) : item[header] || 'N/A'}
                </td>
              ))}
            </tr>
          ))}
        </tbody>
      </table>
    );
  };

  return (
    <div className="section">
      <h2>Section 3</h2>
      {data.length > 0 ? (
        renderTable()
      ) : (
        <p>No valid data available.</p>
      )}
    </div>
  );
}

export default Section3;
