import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './section3.css';

function Section3() {
  const currentDate = new Date();
  const initialDate = currentDate.toISOString().split('T')[0]; // Get the current date in 'YYYY-MM-DD' format
  const initialTime = currentDate.toTimeString().split(' ')[0]; // Get the current time in 'HH:mm:ss' format

  const [data, setData] = useState([]);
  const [updatedData, setUpdatedData] = useState({});
  const [birthdayDate, setBirthdayDate] = useState(initialDate);
  const [birthdayTime, setBirthdayTime] = useState(initialTime);

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:8000/votes'); // Replace with your FastAPI endpoint
      console.log('Response data:', response.data);
      setData(response.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const formatDate = (dateString) => {
    const options = { year: 'numeric', month: 'long', day: 'numeric', hour: 'numeric', minute: 'numeric', second: 'numeric' };
    return new Date(dateString).toLocaleDateString(undefined, options);
  };

  const handleUpdate = async (id) => {
    try {
      const updatedVote = updatedData[id];
      console.log('Sending update data:', updatedVote);
      print(updatedVote)

      // Make a PUT request to update the vote with the given ID
      const response = await axios.put(`http://localhost:8000/update_vote/${id}`, updatedVote);
      console.log('Update response:', response.data);

      // Handle the response as needed
      console.log('Update response:', response.data);

      // Refetch data after updating
      fetchData();
    } catch (error) {
      console.error('Error updating data:', error);
    }
  };

  const handleInputChange = (id, field, value) => {
    // Update the updatedData object with the new value
    setUpdatedData((prevData) => ({
      ...prevData,
      [id]: {
        ...prevData[id],
         [field]: field === 'birthday' ? new Date(`${birthdayDate} ${birthdayTime}`) : value,
        //[field]: field === 'birthday' ? new Date(`${birthdayDate}T${birthdayTime}:00Z`).toISOString() : value,
      },
    }));
  };

  return (
    <div className="section">
      <h2>Section 3</h2>
      <button onClick={fetchData}>Fetch Votes</button>
      <table>
        <thead>
          <tr>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Gender</th>
            <th>Size</th>
            <th>Weight</th>
            <th>Birthday Date</th>
            <th>Birthday Time</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item.id}>
              <td>
                <input
                  type="text"
                  value={updatedData[item.id]?.first_name ?? item.first_name}
                  onChange={(e) => handleInputChange(item.id, 'first_name', e.target.value)}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={updatedData[item.id]?.last_name ?? item.last_name}
                  onChange={(e) => handleInputChange(item.id, 'last_name', e.target.value)}
                />
              </td>
              <td>
                <input
                  type="text"
                  value={updatedData[item.id]?.gender ?? item.gender}
                  onChange={(e) => handleInputChange(item.id, 'gender', e.target.value)}
                />
              </td>
              <td>
                <input
                  type="number"
                  value={updatedData[item.id]?.size ?? item.size}
                  onChange={(e) => handleInputChange(item.id, 'size', parseFloat(e.target.value))}
                />
              </td>
              <td>
                <input
                  type="number"
                  value={updatedData[item.id]?.weight ?? item.weight}
                  onChange={(e) => handleInputChange(item.id, 'weight', parseFloat(e.target.value))}
                />
              </td>
              <td>
                <input
                  type="date"
                  value={birthdayDate}
                  onChange={(e) => setBirthdayDate(e.target.value)}
                />
              </td>
              <td>
                <input
                  type="time"
                  value={birthdayTime}
                  onChange={(e) => setBirthdayTime(e.target.value)}
                />
              </td>
              <td>
                <button onClick={() => handleUpdate(item.id)}>Update</button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Section3;
