import React, { useEffect, useState } from 'react';
import axios from 'axios';
import './section3.css';

function Section3() {
  const [data, setData] = useState([]);
  const [updatedData, setUpdatedData] = useState({});
  const [editRowId, setEditRowId] = useState(null); // Track the currently edited row ID

  useEffect(() => {
    fetchData();
  }, []);

  const fetchData = async () => {
    try {
      const response = await axios.get('http://127.0.0.1:8000/votes');
      console.log('Response data:', response.data);
      setData(response.data);
    } catch (error) {
      console.error('Error fetching data:', error);
    }
  };

  const formatDate = (dateString) => {
    // ... (formatting logic)
  };

  const handleUpdateClick = (id) => {
    // Set the currently edited row ID to open the edit mode for that row
    setEditRowId(id);
  };

  const handleInputChange = (id, field, value) => {
    // Update the updatedData object
    setUpdatedData((prevData) => ({
      ...prevData,
      [id]: {
        ...prevData[id],
        [field]: value,
      },
    }));
  };

  const handleUpdate = (id) => {
    // Make a PUT request to update the vote with the given ID
    const updatedVote = updatedData[id];

    axios
      .put(`http://localhost:8000/update_vote/{id}`, updatedVote)
      .then((response) => {
        console.log('Update response:', response.data);
        // Clear edit mode
        setEditRowId(null);
        // Refetch data after updating
        fetchData();
      })
      .catch((error) => {
        console.error('Error updating data:', error);
      });
  };

  return (
    <div className="section">
      <h2>Section 3</h2>
      <button onClick={fetchData}>Fetch Votes</button>
      <table>
        <thead>
          <tr>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Gender</th>
            <th>Size</th>
            <th>Weight</th>
            <th>Birthday</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {data.map((item) => (
            <tr key={item.id}>
              <td>
                {editRowId === item.id ? (
                  <input
                    type="text"
                    value={updatedData[item.id]?.first_name ?? item.first_name}
                    onChange={(e) => handleInputChange(item.id, 'first_name', e.target.value)}
                  />
                ) : (
                  item.first_name
                )}
              </td>
              <td>
                {editRowId === item.id ? (
                  <input
                    type="text"
                    value={updatedData[item.id]?.last_name ?? item.last_name}
                    onChange={(e) => handleInputChange(item.id, 'last_name', e.target.value)}
                  />
                ) : (
                  item.last_name
                )}
              </td>
              <td>
                {editRowId === item.id ? (
                  <input
                    type="text"
                    value={updatedData[item.id]?.gender ?? item.gender}
                    onChange={(e) => handleInputChange(item.id, 'gender', e.target.value)}
                  />
                ) : (
                  item.gender
                )}
              </td>
              <td>
                {editRowId === item.id ? (
                  <input
                    type="number"
                    value={updatedData[item.id]?.size ?? item.size}
                    onChange={(e) => handleInputChange(item.id, 'size', parseFloat(e.target.value))}
                  />
                ) : (
                  item.size
                )}
              </td>
              <td>
                {editRowId === item.id ? (
                  <input
                    type="number"
                    value={updatedData[item.id]?.weight ?? item.weight}
                    onChange={(e) => handleInputChange(item.id, 'weight', parseFloat(e.target.value))}
                  />
                ) : (
                  item.weight
                )}
              </td>
              <td>
              {editRowId === item.id ? (
              <>
                <input
                type="date"
                value={updatedData[item.id]?.birthday ?? item.birthday.split(' ')[0]} // Extract the date part
                onChange={(e) => handleInputChange(item.id, 'birthday', e.target.value)}
              />
                <input
                type="time"
                value={updatedData[item.id]?.birthday ? updatedData[item.id].birthday.split(' ')[1] : '00:00'} // Extract the time part
                onChange={(e) => {
                  const newTime = e.target.value;
                  // Combine the updated date and time
                  const updatedDateTime = `${updatedData[item.id]?.birthday?.split(' ')[0] || item.birthday.split(' ')[0]} ${newTime}`;
                  handleInputChange(item.id, 'birthday', updatedDateTime);
            }}
      />
    </>
                ) : (
                  formatDate(item.birthday)
                )}
              </td>
              <td>
                {editRowId === item.id ? (
                  <button onClick={() => handleUpdate(item.id)}>Update</button>
                ) : (
                  <button onClick={() => handleUpdateClick(item.id)}>Edit</button>
                )}
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default Section3;
